  impersonatedAddress: createAddress(&#x27;0x1234567890123456789012345678901234567890&#x27;),
  to: contractAddress,
  data: EMIT_EVENT_SELECTOR, // Function selector that emits events
  gasLimit: 100000n,
})

await runTx(vm)({ tx: interactTx })

