      &lt;/div&gt;
    {/if}
  &lt;/div&gt;

  &lt;div class=&quot;errors&quot;&gt;
    &lt;h3&gt;Errors&lt;/h3&gt;
    {#each errors as error}
      &lt;div class=&quot;error&quot;&gt;{error}&lt;/div&gt;
    {/each}
